sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({
    api,
    event,
    helpers,
    imports
}) {

    const {
        context,
        state,
        setState
    } = api;

    const {
        snHttp
    } = helpers;

    const curentArticle = context.props.sysId;

    //getting feedback from article

    snHttp('/api/now/table/kb_feedback?sysparm_limit=1&sysparm_query=article=' + curentArticle, {
        method: "GET",
        batch: false,
    }).then(response => {
        if (response.response.result.length > 0) {
            var rating = response.response.result[0].rating;

            //updating state
            setState('rating', rating);
        }
    }).catch(error => {
        console.log('Error' + error);
    });

}
